%flink

import org.apache.flink.streaming.api.functions.sink.PrintSinkFunction
import org.apache.flink.api.java.utils.ParameterTool
import org.apache.flink.api.common.functions._
import org.apache.flink.api.common.typeinfo.TypeInformation
import org.apache.flink.api.scala._
import org.apache.flink.streaming.api.scala._
import org.apache.flink.streaming.connectors.kafka.{FlinkKafkaConsumer, FlinkKafkaProducer}
import org.apache.flink.streaming.util.serialization.SerializationSchema
import org.apache.flink.formats.json.JsonNodeDeserializationSchema
import scala.collection.JavaConverters.asScalaIteratorConverter
import java.util.Properties
import org.apache.flink.shaded.jackson2.com.fasterxml.jackson.databind.node.ObjectNode
import java.beans.Transient

class ObjectNodeSerSchema extends SerializationSchema[ObjectNode] with Serializable {
    override def serialize(t: ObjectNode): Array[Byte] = t.toString().getBytes("UTF-8")   
}

class BerkaPaymentsDemo extends Serializable {

     sys.env("HOME")


    // Extract SELF IP from Eskimo Topology (env var)
    var selfIp = sys.env("SELF_IP_ADDRESS").replace(".", "")

    // Extract KAFKA Master IP Address
    var kafkaMasterIp = sys.env("SELF_MASTER_KAFKA_" + selfIp)

    var kafkaServer = kafkaMasterIp + ":9092"

    val properties = new Properties()
    properties.setProperty("bootstrap.servers", kafkaServer)
    properties.setProperty("group.id", "test")

    @Transient lazy val timestampParseFormat = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ")
    // 1997-10-10T00:00:00+0000

    @Transient lazy val dayOfWeekFormat = new java.text.SimpleDateFormat("E")
    @Transient lazy val dayOfMonthFormat = new java.text.SimpleDateFormat("dd")

    implicit val typeInfoObjectNode = TypeInformation.of(classOf[org.apache.flink.shaded.jackson2.com.fasterxml.jackson.databind.node.ObjectNode])
    implicit val typeInfoString = TypeInformation.of(classOf[String])

    @Transient lazy val aggregatePaymentsKafkaProducer = new FlinkKafkaProducer[ObjectNode](
        kafkaServer,     // broker list
        "berka-payments-aggregate",               // target topic
        new ObjectNodeSerSchema())   // serialization schema

    //: DataStream[ObjectNode]
    @Transient lazy val kafkaBerkaPaymentsStream = senv.addSource(
                    new FlinkKafkaConsumer("berka-payments", new JsonNodeDeserializationSchema(), properties))

    def addDayOfWeek(node: ObjectNode) : ObjectNode = {
        val timestampDate = timestampParseFormat.parse(node.get("@timestamp").asText())
        node.put("day_of_week", dayOfWeekFormat.format(timestampDate))
        return node
    }

    def addDayOfMonth(node: ObjectNode) : ObjectNode = {
        val timestampDate = timestampParseFormat.parse(node.get("@timestamp").asText())
        node.put("day_of_month", dayOfMonthFormat.format(timestampDate))
        return node
    }

    def main(args: Array[String]) {

        senv.setParallelism(1);

        kafkaBerkaPaymentsStream
            .map(node => addDayOfWeek(node))
            .map(node => addDayOfMonth(node))
            .addSink(aggregatePaymentsKafkaProducer)
            //.print()

        senv.execute("Berka Payment Transformation")

    }

}

new BerkaPaymentsDemo().main(Array())
